import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 1-505 Seals deemed property of Commonwealth; unauthorized use; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/1-505.md","filePath":"vacode/1-505.md"}'),r={name:"vacode/1-505.md"},n=t('<h1 id="§-1-505-seals-deemed-property-of-commonwealth-unauthorized-use-penalty" tabindex="-1">§ 1-505 Seals deemed property of Commonwealth; unauthorized use; penalty. <a class="header-anchor" href="#§-1-505-seals-deemed-property-of-commonwealth-unauthorized-use-penalty" aria-label="Permalink to &quot;§ 1-505 Seals deemed property of Commonwealth; unauthorized use; penalty.&quot;">​</a></h1><p>The seals of the Commonwealth shall be deemed the property of the Commonwealth; and no persons shall exhibit, display, or in any manner utilize the seals or any facsimile or representation of the seals of the Commonwealth for nongovernmental purposes unless such use is specifically authorized by law.</p><p>Except for the authorized commercial use of the seal as provided in § <a href="http://law.lis.virginia.gov/vacode/2.2-122/" target="_blank" rel="noreferrer">2.2-122</a>, any person violating the provisions of this section shall be punished by a fine of not more than $100, or by imprisonment for not more than 30 days or both.</p><p>1966, c. 102, § 7.1-31.1; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0295" target="_blank" rel="noreferrer">295</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0839" target="_blank" rel="noreferrer">839</a>.</p>',4),s=[n];function i(l,h,p,d,m,c){return o(),a("div",null,s)}const _=e(r,[["render",i]]);export{u as __pageData,_ as default};
