import{_ as e,o as a,c as r,O as o}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 1-508 Director of General Services to have available flags of the Commonwealth for sale.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/1-508.md","filePath":"vacode/1-508.md"}'),t={name:"vacode/1-508.md"},l=o('<h1 id="§-1-508-director-of-general-services-to-have-available-flags-of-the-commonwealth-for-sale" tabindex="-1">§ 1-508 Director of General Services to have available flags of the Commonwealth for sale. <a class="header-anchor" href="#§-1-508-director-of-general-services-to-have-available-flags-of-the-commonwealth-for-sale" aria-label="Permalink to &quot;§ 1-508 Director of General Services to have available flags of the Commonwealth for sale.&quot;">​</a></h1><p>The Director of the Department of General Services shall have available at all times flags of the Commonwealth, to be offered for sale to the public in such manner as the Director may determine.</p><p>Such flags shall be of good quality, shall conform to the specifications prescribed in § <a href="http://law.lis.virginia.gov/vacode/1-506/" target="_blank" rel="noreferrer">1-506</a>, shall be offered in the various sizes prescribed by the Governor pursuant to § <a href="http://law.lis.virginia.gov/vacode/1-507/" target="_blank" rel="noreferrer">1-507</a>, and shall be purchased in compliance with the provisions of § <a href="http://law.lis.virginia.gov/vacode/2.2-4323.1/" target="_blank" rel="noreferrer">2.2-4323.1</a>.</p><p>The prices to be charged for such flags shall be at cost as determined by the Director.</p><p>Code 1950, § 7-33.1; 1958, c. 553; 1960, c. 347; 1966, c. 102, § 7.1-34; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0839" target="_blank" rel="noreferrer">839</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0289" target="_blank" rel="noreferrer">289</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0297" target="_blank" rel="noreferrer">297</a>.</p>',5),i=[l];function s(n,c,h,f,v,p){return a(),r("div",null,i)}const _=e(t,[["render",s]]);export{d as __pageData,_ as default};
