import{_ as t,o as n,c as s,z as e,a as o}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 1-603 Plane coordinates used in systems.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/1-603.md","filePath":"vacode/1-603.md"}'),a={name:"vacode/1-603.md"},i=e("h1",{id:"§-1-603-plane-coordinates-used-in-systems",tabindex:"-1"},[o("§ 1-603 Plane coordinates used in systems. "),e("a",{class:"header-anchor",href:"#§-1-603-plane-coordinates-used-in-systems","aria-label":'Permalink to "§ 1-603 Plane coordinates used in systems."'},"​")],-1),r=e("p",null,`The plane coordinates of a point on the earth's surface, to be used in expressing the position or location of such point in the appropriate zone of these systems, shall be expressed in U.S. survey feet and decimals of a foot. One of these distances, to be known as the "x-coordinate," shall give the position in an east-and-west direction; the other, to be known as the "y-coordinate," shall give the position in a north-and-south direction. These coordinates shall be made to depend upon and conform to the coordinate values for the monumented points of the North American Horizontal Geodetic Control Network as published by the National Ocean Service/National Geodetic Survey, or its successors, and whose plane coordinates have been computed on the systems defined in this chapter. Any such station may be used for establishing a survey connection to either Virginia coordinate system.`,-1),c=e("p",null,"When converting coordinates in the Virginia Coordinate System of 1983 from meters and decimals of a meter to feet and decimals of a foot, the U.S. survey foot conversion factor (one foot equals 1200/3937 meters) shall be used. This requirement does not preclude the continued use of the International foot conversion factor (one foot equals 0.3048 meters) in those counties and cities where this factor was in use prior to July 1, 1992. The plat or plan shall contain a statement of the conversion factor used and the coordinate values of a minimum of two project points in feet.",-1),d=e("p",null,[o("1946, p. 167; Michie Suppl. 1946, § 2849(3); Code 1950, § 55-290; 1984, c. 726; 1992, c. 1; 2019, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712",target:"_blank",rel:"noreferrer"},"712"),o(".")],-1),h=[i,r,c,d];function l(p,f,u,m,_,v){return n(),s("div",null,h)}const g=t(a,[["render",l]]);export{b as __pageData,g as default};
