import{_ as a,o as n,c as s,z as e,a as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 10.1-1027 Wild Spanish Mustangs Fund established; administration; purpose.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-1027.md","filePath":"vacode/10.1-1027.md"}'),i={name:"vacode/10.1-1027.md"},r=e("h1",{id:"§-10-1-1027-wild-spanish-mustangs-fund-established-administration-purpose",tabindex:"-1"},[t("§ 10.1-1027 Wild Spanish Mustangs Fund established; administration; purpose. "),e("a",{class:"header-anchor",href:"#§-10-1-1027-wild-spanish-mustangs-fund-established-administration-purpose","aria-label":'Permalink to "§ 10.1-1027 Wild Spanish Mustangs Fund established; administration; purpose."'},"​")],-1),o=e("p",null,"There is hereby established in the state treasury a special nonreverting fund to be known as the Wild Spanish Mustangs Fund, hereafter referred to as the Fund. The Fund shall consist of such moneys as may be appropriated by the General Assembly and such other moneys as may be made available from any other source, public or private. Interest earned on moneys in the Fund shall remain in the Fund and be credited to it. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund, but shall remain in the Fund. The Fund shall be administered by the Department of Conservation and Recreation. Any expenditure from the Fund shall be subject to the recommendations of the Park Manager at False Cape State Park, with advice and consultation from the City of Virginia Beach, local legislators, and interested community members. The Fund shall be used for the general purpose of protecting the herd of wild Spanish mustangs on the barrier islands of Virginia. Allocations may include, but are not limited to, the erection and maintenance of fences to restrict the entrance of wild horses into Virginia, the transporting of any wild horses that do reach Virginia back to North Carolina, and other measures to protect these horses and promote their retention in North Carolina, as determined by the Department of Conservation and Recreation.",-1),d=e("p",null,[t("2007, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0037",target:"_blank",rel:"noreferrer"},"37"),t(".")],-1),h=[r,o,d];function l(c,u,p,m,f,b){return n(),s("div",null,h)}const y=a(i,[["render",l]]);export{_ as __pageData,y as default};
