import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 10.1-104.9 Regulations under this article.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-104.9.md","filePath":"vacode/10.1-104.9.md"}'),i={name:"vacode/10.1-104.9.md"},o=r('<h1 id="§-10-1-104-9-regulations-under-this-article" tabindex="-1">§ 10.1-104.9 Regulations under this article. <a class="header-anchor" href="#§-10-1-104-9-regulations-under-this-article" aria-label="Permalink to &quot;§ 10.1-104.9 Regulations under this article.&quot;">​</a></h1><p>Regulations adopted by the Board for the enforcement of this article shall be subject to the requirements set out in §§ <a href="http://law.lis.virginia.gov/vacode/2.2-4007.03/" target="_blank" rel="noreferrer">2.2-4007.03</a>, <a href="http://law.lis.virginia.gov/vacode/2.2-4007.04/" target="_blank" rel="noreferrer">2.2-4007.04</a>, <a href="http://law.lis.virginia.gov/vacode/2.2-4007.05/" target="_blank" rel="noreferrer">2.2-4007.05</a>, and <a href="http://law.lis.virginia.gov/vacode/2.2-4026/" target="_blank" rel="noreferrer">2.2-4026</a> through <a href="http://law.lis.virginia.gov/vacode/2.2-4030/" target="_blank" rel="noreferrer">2.2-4030</a> of the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.), and shall be published in the Virginia Register of Regulations. The Board shall convene a stakeholder group to assist in development of these regulations, with representation from agricultural and environmental interests as well as Soil and Water Conservation Districts. All other provisions of the Administrative Process Act shall not apply to the adoption of any regulation pursuant to this article. After the close of the 60-day comment period, the Board may adopt a final regulation, with or without changes. Such regulation shall become effective 15 days after publication in the Virginia Register of Regulations, unless the Board has withdrawn or suspended the regulation or a later date has been set by the Board. The Board shall also hold at least one public hearing on the proposed regulation during the 60-day comment period. The notice for such public hearing shall include the date, time, and place of the hearing.</p><p>2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0781" target="_blank" rel="noreferrer">781</a>.</p>',3),n=[o];function l(s,h,d,c,g,u){return t(),a("div",null,n)}const _=e(i,[["render",l]]);export{f as __pageData,_ as default};
