import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 10.1-1122 Management, harvesting, sale of timber on state-owned land.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-1122.md","filePath":"vacode/10.1-1122.md"}'),r={name:"vacode/10.1-1122.md"},o=n('<h1 id="§-10-1-1122-management-harvesting-sale-of-timber-on-state-owned-land" tabindex="-1">§ 10.1-1122 Management, harvesting, sale of timber on state-owned land. <a class="header-anchor" href="#§-10-1-1122-management-harvesting-sale-of-timber-on-state-owned-land" aria-label="Permalink to &quot;§ 10.1-1122 Management, harvesting, sale of timber on state-owned land.&quot;">​</a></h1><p>A. The Department in cooperation with the Division of Engineering and Buildings shall develop a forest management plan for state-owned lands with the assistance of affected state agencies, departments and institutions.</p><p>B. Prior to the sale of timber from state-owned lands, the proposed sale shall be first approved by the Department and by the Division of Engineering and Buildings. The Department shall make or arrange for all sales so approved and shall deposit all proceeds to the credit of the Fund, except that when sales are made from timber on land held by special fund agencies or the Department of Military Affairs, or from timber on land that is gift property specified in subsection I of § <a href="/vacode/2.2-1156/">2.2-1156</a>, the Department shall deposit in the Fund only so much of the proceeds as are needed to defray the cost of the sale and to implement the forestry management plan on that particular tract of land. The remainder of the proceeds from such a sale shall then be paid over to the special fund agency concerned, the Department of Military Affairs, or the agency or institution holding the gift properties, to be used for the purposes of that agency, department, or institution.</p><p>1980, c. 525, § 10-45.3; 1981, c. 219; 1986, c. 567; 1988, c. 891; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0612" target="_blank" rel="noreferrer">612</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0659" target="_blank" rel="noreferrer">659</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0660" target="_blank" rel="noreferrer">660</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0761" target="_blank" rel="noreferrer">761</a>.</p>',4),i=[o];function s(l,d,h,f,c,p){return t(),a("div",null,i)}const _=e(r,[["render",s]]);export{m as __pageData,_ as default};
