import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 10.1-113 Sale of trees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-113.md","filePath":"vacode/10.1-113.md"}'),o={name:"vacode/10.1-113.md"},i=r('<h1 id="§-10-1-113-sale-of-trees" tabindex="-1">§ 10.1-113 Sale of trees. <a class="header-anchor" href="#§-10-1-113-sale-of-trees" aria-label="Permalink to &quot;§ 10.1-113 Sale of trees.&quot;">​</a></h1><p>For the purpose of managing Departmental lands or maintaining the production of forest products in Departmental lands, the Director, upon the recommendation of the State Forester, may designate and appraise trees to be cut under the principles of scientific forest management, and may sell them for not less than their appraised value. When the appraised value of the trees to be sold is more than $50,000, the Director, before selling them, shall receive bids, after notice by publication once a week for two weeks in two newspapers of general circulation; but the Director shall have the right to reject any and all bids and to readvertise for bids. The proceeds arising from the sale of the timber and trees from state park lands shall be paid into the State Park Conservation Resources Fund established in subsection A of § <a href="http://law.lis.virginia.gov/vacode/10.1-202/" target="_blank" rel="noreferrer">10.1-202</a>. The proceeds arising from the sale of the timber and trees from natural area preserves owned by the Department in fee simple shall be paid into the Natural Area Preservation Fund established in § <a href="http://law.lis.virginia.gov/vacode/10.1-215/" target="_blank" rel="noreferrer">10.1-215</a>.</p><p>1988, c. 891; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0079" target="_blank" rel="noreferrer">79</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0089" target="_blank" rel="noreferrer">89</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0158" target="_blank" rel="noreferrer">158</a>.</p>',3),n=[i];function s(l,h,c,d,f,p){return a(),t("div",null,n)}const m=e(o,[["render",s]]);export{g as __pageData,m as default};
