import{_ as i,o,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 10.1-1144 Failure to clean premises of certain mills.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-1144.md","filePath":"vacode/10.1-1144.md"}'),r={name:"vacode/10.1-1144.md"},s=e("h1",{id:"§-10-1-1144-failure-to-clean-premises-of-certain-mills",tabindex:"-1"},[t("§ 10.1-1144 Failure to clean premises of certain mills. "),e("a",{class:"header-anchor",href:"#§-10-1-1144-failure-to-clean-premises-of-certain-mills","aria-label":'Permalink to "§ 10.1-1144 Failure to clean premises of certain mills."'},"​")],-1),n=e("p",null,"Any individual, firm, or corporation responsible for the operation of a saw mill, stave mill, heading mill, or any other mill in, through or near forest or brushland shall clean the premises for at least a distance of fifty yards in all directions from any fires maintained in or about, or in connection with the operation of such mill. The premises shall also be cleaned for a distance of 100 feet in all directions from any sawdust pile, slab pile, or any other inflammable material which accumulates from the operation of such mill, or all matter not essential to the operation of such mill, which is liable to take fire from any sparks emitted from such fires. When any mill is removed or ceases to operate for a period of ten consecutive days, any fire which may be burning in any sawdust pile, slab pile or other debris shall be totally extinguished unless the owner of the land on which such fire is located assumes in writing responsibility for the control of the fire. Any person, firm or corporation violating any of the provisions of this section shall be guilty of a Class 4 misdemeanor. Each day or fraction thereof on which any such mill is operated in violation of the provisions of this section and each day or fraction thereof on which fire is allowed to burn in any sawdust pile, slab pile or other inflammable debris in violation of the provisions of this section, shall be deemed a separate offense.",-1),l=e("p",null,"Whenever it is established that a forest fire originated from a fire maintained in or about any such mill, the individual, firm, or corporation, from whose mill any such fire originated shall immediately become liable for all costs incurred in fighting such fire.",-1),c=e("p",null,"Code 1950, § 10-64; 1986, c. 188; 1988, c. 891.",-1),h=[s,n,l,c];function f(d,m,p,u,b,y){return o(),a("div",null,h)}const w=i(r,[["render",f]]);export{v as __pageData,w as default};
