import{_ as e,o as a,c as t,z as o,a as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 10.1-1147 Removal of inflammable material from lands adjoining right-of-way by railroads.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-1147.md","filePath":"vacode/10.1-1147.md"}'),n={name:"vacode/10.1-1147.md"},r=o("h1",{id:"§-10-1-1147-removal-of-inflammable-material-from-lands-adjoining-right-of-way-by-railroads",tabindex:"-1"},[i("§ 10.1-1147 Removal of inflammable material from lands adjoining right-of-way by railroads. "),o("a",{class:"header-anchor",href:"#§-10-1-1147-removal-of-inflammable-material-from-lands-adjoining-right-of-way-by-railroads","aria-label":'Permalink to "§ 10.1-1147 Removal of inflammable material from lands adjoining right-of-way by railroads."'},"​")],-1),s=o("p",null,"For the purpose of providing increased protection to forest property from fire originating along railroads, any railroad company shall have the right, subject to the provisions of this section, without liability for trespass to enter upon forest or brushlands for a distance of fifty feet from the railroad right-of-way and to clear from such a strip any inflammable material such as leaves, grass, dead trees, slash and brush, but shall not remove any valuable timber growth or other things of value without consent of and recompense to the owner. Not less than fifteen days prior to clearing such land, the railroad company shall give the owner notice of its intention, together with a transcript of this section, by letter deposited in the United States mail to his last known address. If the owner does not file objections to such clearings with the State Corporation Commission within ten days of the date of such notice he shall be deemed to have given consent. Upon the filing by an owner of such objection showing cause why such clearing should not be done the State Corporation Commission shall review the case and may sustain the objection of the owner or permit the clearing in whole or in part.",-1),l=o("p",null,"The State Corporation Commission may require assistance of the State Forester in furnishing information pertinent to the administration of this section.",-1),h=o("p",null,"The provisions of this section shall not apply to temporary tram roads used for hauling logs and lumber.",-1),d=o("p",null,"Code 1950, § 10-66; 1988, c. 891.",-1),f=[r,s,l,h,d];function c(m,p,u,g,b,y){return a(),t("div",null,f)}const w=e(n,[["render",c]]);export{v as __pageData,w as default};
