import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 10.1-117 Powers and duties of conservation officers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-117.md","filePath":"vacode/10.1-117.md"}'),r={name:"vacode/10.1-117.md"},n=a('<h1 id="§-10-1-117-powers-and-duties-of-conservation-officers" tabindex="-1">§ 10.1-117 Powers and duties of conservation officers. <a class="header-anchor" href="#§-10-1-117-powers-and-duties-of-conservation-officers" aria-label="Permalink to &quot;§ 10.1-117 Powers and duties of conservation officers.&quot;">​</a></h1><p>A. It shall be the duty of all conservation officers to uphold and enforce the laws of the Commonwealth, the regulations of the Department, and the rules and regulations of the Breaks Interstate Park Commission.</p><p>B. Commissioned conservation officers shall be law-enforcement officers and shall have the power to enforce the laws of the Commonwealth, the regulations of the Department and the collegial bodies under administrative support of the Department, and the rules and regulations of the Breaks Interstate Park Commission. If requested by the chief law-enforcement officer of the locality, conservation officers shall coordinate the investigation of felonies with the local law-enforcement agency.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0205" target="_blank" rel="noreferrer">205</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0088" target="_blank" rel="noreferrer">88</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0064" target="_blank" rel="noreferrer">64</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0489" target="_blank" rel="noreferrer">489</a>.</p>',4),i=[n];function s(l,f,c,h,d,p){return t(),o("div",null,i)}const m=e(r,[["render",s]]);export{g as __pageData,m as default};
