import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 10.1-1186.1:1 Hazardous Waste Site Inventory.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-1186.1:1.md","filePath":"vacode/10.1-1186.1:1.md"}'),o={name:"vacode/10.1-1186.1:1.md"},i=n('<h1 id="§-10-1-1186-1-1-hazardous-waste-site-inventory" tabindex="-1">§ 10.1-1186.1:1 Hazardous Waste Site Inventory. <a class="header-anchor" href="#§-10-1-1186-1-1-hazardous-waste-site-inventory" aria-label="Permalink to &quot;§ 10.1-1186.1:1 Hazardous Waste Site Inventory.&quot;">​</a></h1><p>A. The Department shall compile and maintain a Hazardous Waste Site Inventory (the Inventory) comprising a current listing of sites permitted by or in corrective action under the Department at which the disposal of hazardous waste, as defined in § <a href="/vacode/10.1-1400/">10.1-1400</a> and not otherwise excluded from regulation as hazardous waste, has occurred. The Inventory shall contain specific information about each listed site, including (i) the location of the site, (ii) the nature and known characteristics of the wastes disposed of at the site, and (iii) the status of any remedial or corrective action undertaken or planned for the site. The Department shall only disclose in the Inventory information that is not otherwise subject to an exemption from the Virginia Freedom of Information Act (§ <a href="/vacode/2.2-3700/">2.2-3700</a> et seq.).</p><p>B. The Department shall publish the Inventory by July 1, 2021, update it at least annually thereafter, and post it on the Department&#39;s website.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0491" target="_blank" rel="noreferrer">491</a>.</p>',4),r=[i];function s(c,d,h,l,p,_){return t(),a("div",null,r)}const m=e(o,[["render",s]]);export{f as __pageData,m as default};
