import{_ as e,o as t,c as n,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 10.1-1187.1 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-1187.1.md","filePath":"vacode/10.1-1187.1.md"}'),a={name:"vacode/10.1-1187.1.md"},i=o('<h1 id="§-10-1-1187-1-definitions" tabindex="-1">§ 10.1-1187.1 Definitions. <a class="header-anchor" href="#§-10-1-1187-1-definitions" aria-label="Permalink to &quot;§ 10.1-1187.1 Definitions.&quot;">​</a></h1><p>&quot;Board or Boards&quot; means the State Air Pollution Control Board, the State Water Control Board, and the Virginia Waste Management Board.</p><p>&quot;Department&quot; means the Department of Environmental Quality.</p><p>&quot;Director&quot; means the Director of the Department of Environmental Quality.</p><p>&quot;Environmental Management System&quot; means a comprehensive, cohesive set of documented policies and procedures adopted by a facility or person and used to establish environmental goals, to meet and maintain those goals, to evaluate environmental performance and to achieve measurable or noticeable improvements in environmental performance, through planning, documented management and operational practices, operational changes, self assessments, and management review. The term shall include, but not be limited to, any such system developed in accordance with the International Standards of Operation 14001 standards.</p><p>&quot;E2&quot; means an environmental enterprise.</p><p>&quot;E3&quot; means an exemplary environmental enterprise.</p><p>&quot;E4&quot; means an extraordinary environmental enterprise.</p><p>&quot;Facility&quot; means a manufacturing, business, agricultural, or governmental site or installation involving one or more contiguous buildings or structures under common ownership or management.</p><p>&quot;Record of sustained compliance&quot; means that the person or facility (i) has no judgment or conviction entered against it, or against any key personnel of the person or facility or any person with an ownership interest in the facility for a criminal violation of environmental protection laws of the United States, the Commonwealth, or any other state in the previous five years; (ii) has been neither the cause of, nor liable for, more than two significant environmental violations in the previous three years; (iii) has no unresolved notices of violations or potential violations of environmental requirements with the Department or one of the Boards; (iv) is in compliance with the terms of any order or decree, executive compliance agreement, or related enforcement measure issued by the Department, one of the Boards, or the U.S. Environmental Protection Agency; and (v) has not demonstrated in any other way an unwillingness or inability to comply with environmental protection requirements.</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0705" target="_blank" rel="noreferrer">705</a>.</p>',11),r=[i];function s(l,m,c,p,d,h){return t(),n("div",null,r)}const v=e(a,[["render",s]]);export{f as __pageData,v as default};
