import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 10.1-1188.1 Department of Transportation to consider wildlife corridors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-1188.1.md","filePath":"vacode/10.1-1188.1.md"}'),a={name:"vacode/10.1-1188.1.md"},i=o('<h1 id="§-10-1-1188-1-department-of-transportation-to-consider-wildlife-corridors" tabindex="-1">§ 10.1-1188.1 Department of Transportation to consider wildlife corridors. <a class="header-anchor" href="#§-10-1-1188-1-department-of-transportation-to-consider-wildlife-corridors" aria-label="Permalink to &quot;§ 10.1-1188.1 Department of Transportation to consider wildlife corridors.&quot;">​</a></h1><p>The Department of Transportation (VDOT) shall, as part of the environmental review it conducts for a road or highway construction project, include in an environmental impact statement a list of any existing terrestrial or aquatic wildlife corridor identified in the Wildlife Corridor Action Plan (the Plan) created pursuant to Article 8 (§ <a href="/vacode/29.1-578/">29.1-578</a> et seq.) of Chapter 5 of Title 29.1 that will be affected by such construction project. In the design options for any road or highway construction project that threatens wildlife connectivity in a corridor identified in the Plan, VDOT shall consider measures for the mitigation of harm caused by such road to terrestrial and aquatic wildlife.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0323" target="_blank" rel="noreferrer">323</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0672" target="_blank" rel="noreferrer">672</a>.</p>',3),n=[i];function c(s,d,l,f,p,h){return t(),r("div",null,n)}const u=e(a,[["render",c]]);export{m as __pageData,u as default};
