import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 10.1-1197.7 Review and authorization of projects.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-1197.7.md","filePath":"vacode/10.1-1197.7.md"}'),r={name:"vacode/10.1-1197.7.md"},o=i('<h1 id="§-10-1-1197-7-review-and-authorization-of-projects" tabindex="-1">§ 10.1-1197.7 Review and authorization of projects. <a class="header-anchor" href="#§-10-1-1197-7-review-and-authorization-of-projects" aria-label="Permalink to &quot;§ 10.1-1197.7 Review and authorization of projects.&quot;">​</a></h1><p>A. Upon submission of a complete application, the Department, after consultation with other agencies in the Secretariat of Natural and Historic Resources before authorizing the project, shall conduct an assessment of whether the application meets the requirements of the applicable permit by rule regulations. If the Department determines that the application is deficient, it promptly shall notify the applicant in writing and specify the deficiencies.</p><p>B. Any interested party, including an applicant for a permit, who has participated in a proceeding for a permit to construct or operate a small renewable energy project under procedures adopted by the Department pursuant to this section, and who is aggrieved by the final decision of the Department, shall have the remedies provided by the Administrative Process Act (§ <a href="/vacode/2.2-4000/">2.2-4000</a> et seq.).</p><p>2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0808" target="_blank" rel="noreferrer">808</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0854" target="_blank" rel="noreferrer">854</a>.</p>',4),n=[o];function s(c,p,h,l,d,f){return t(),a("div",null,n)}const m=e(r,[["render",s]]);export{u as __pageData,m as default};
