import{_ as e,o as a,c as o,z as i,a as t}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 10.1-1199 Immunity against administrative or civil penalties for voluntarily disclosed violation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-1199.md","filePath":"vacode/10.1-1199.md"}'),n={name:"vacode/10.1-1199.md"},r=i("h1",{id:"§-10-1-1199-immunity-against-administrative-or-civil-penalties-for-voluntarily-disclosed-violation",tabindex:"-1"},[t("§ 10.1-1199 Immunity against administrative or civil penalties for voluntarily disclosed violation. "),i("a",{class:"header-anchor",href:"#§-10-1-1199-immunity-against-administrative-or-civil-penalties-for-voluntarily-disclosed-violation","aria-label":'Permalink to "§ 10.1-1199 Immunity against administrative or civil penalties for voluntarily disclosed violation."'},"​")],-1),s=i("p",null,"To the extent consistent with requirements imposed by federal law, any person making a voluntary disclosure of information to a state or local regulatory agency regarding a violation of an environmental statute, regulation, permit or administrative order shall be accorded immunity from administrative or civil penalty under such statute, regulation, permit or administrative order. A disclosure is voluntary if (i) it is not otherwise required by law, regulation, permit or administrative order, (ii) it is made promptly after knowledge of the violation is obtained through a voluntary environmental assessment, and (iii) the person making the disclosure corrects the violation in a diligent manner in accordance with a compliance schedule submitted to the appropriate state or local regulatory agencies demonstrating such diligence. Immunity shall not be accorded if it is found that the person making the voluntary disclosure has acted in bad faith. This section does not bar the institution of a civil action claiming compensation for injury to person or property against an owner or operator.",-1),l=i("p",null,[t("1995, c. "),i("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0564",target:"_blank",rel:"noreferrer"},"564"),t(".")],-1),d=[r,s,l];function c(m,u,v,p,h,f){return a(),o("div",null,d)}const _=e(n,[["render",c]]);export{y as __pageData,_ as default};
