import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 10.1-1322.4 Permit modifications for alternative fuels or raw materials.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-1322.4.md","filePath":"vacode/10.1-1322.4.md"}'),i={name:"vacode/10.1-1322.4.md"},o=t('<h1 id="§-10-1-1322-4-permit-modifications-for-alternative-fuels-or-raw-materials" tabindex="-1">§ 10.1-1322.4 Permit modifications for alternative fuels or raw materials. <a class="header-anchor" href="#§-10-1-1322-4-permit-modifications-for-alternative-fuels-or-raw-materials" aria-label="Permalink to &quot;§ 10.1-1322.4 Permit modifications for alternative fuels or raw materials.&quot;">​</a></h1><p>Unless required by federal law or regulation, no additional permit or permit modifications shall be required for the use, by any source, of an alternative fuel or raw material, if the owner demonstrates that as a result of trial burns at his facility or other facilities or other sufficient data that the emissions resulting from the use of the alternative fuel or raw material supply are decreased. To the extent allowed by federal law or regulation, no demonstration shall be required for the use of processed animal fat, processed fish oil, processed vegetable oil, distillate oil, or any mixture thereof in place of the same quantity of residual oil to fire industrial boilers.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0717" target="_blank" rel="noreferrer">717</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0282" target="_blank" rel="noreferrer">282</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0356" target="_blank" rel="noreferrer">356</a>.</p>',3),l=[o];function s(n,f,c,d,m,u){return a(),r("div",null,l)}const p=e(i,[["render",s]]);export{_ as __pageData,p as default};
