import{_ as e,o as n,c as o,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 10.1-1425.12 Pollution prevention assistance program.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-1425.12.md","filePath":"vacode/10.1-1425.12.md"}'),a={name:"vacode/10.1-1425.12.md"},i=t('<h1 id="§-10-1-1425-12-pollution-prevention-assistance-program" tabindex="-1">§ 10.1-1425.12 Pollution prevention assistance program. <a class="header-anchor" href="#§-10-1-1425-12-pollution-prevention-assistance-program" aria-label="Permalink to &quot;§ 10.1-1425.12 Pollution prevention assistance program.&quot;">​</a></h1><p>The Department shall establish a voluntary pollution prevention assistance program designed to assist all persons in promoting pollution prevention measures in the Commonwealth. The program shall emphasize assistance to local governments and businesses that have inadequate technical and financial resources to obtain information and to assess and implement pollution prevention measures. The program may include, but shall not be limited to:</p><ol><li><p>Establishment of a pollution prevention clearinghouse for all available information concerning waste reduction, waste minimization, source reduction, economic and energy savings, and pollution prevention;</p></li><li><p>Assistance in transferring information concerning pollution prevention technologies through workshops, conferences and handbooks;</p></li><li><p>Cooperation with programs at baccalaureate institutions of higher education to develop pollution prevention curricula and training;</p></li><li><p>Technical assistance to generators of toxic or hazardous substances, including onsite consultation to identify alternative methods that may be applied to prevent pollution; and</p></li><li><p>Researching and recommending incentive programs for innovative pollution prevention programs.</p></li></ol><p>To be eligible for onsite technical assistance, a generator of toxic or hazardous substances must agree to allow information regarding the results of such assistance to be shared with the public, provided that the identity of the generator shall be made available only with its consent and trade-secret information shall remain protected.</p><p>1993, c. 459; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0169" target="_blank" rel="noreferrer">169</a>.</p>',5),r=[i];function s(l,c,p,d,h,u){return n(),o("div",null,r)}const v=e(a,[["render",s]]);export{g as __pageData,v as default};
