import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 10.1-1425.19 Inspections and enforcement actions by the Department.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-1425.19.md","filePath":"vacode/10.1-1425.19.md"}'),r={name:"vacode/10.1-1425.19.md"},i=n('<h1 id="§-10-1-1425-19-inspections-and-enforcement-actions-by-the-department" tabindex="-1">§ 10.1-1425.19 Inspections and enforcement actions by the Department. <a class="header-anchor" href="#§-10-1-1425-19-inspections-and-enforcement-actions-by-the-department" aria-label="Permalink to &quot;§ 10.1-1425.19 Inspections and enforcement actions by the Department.&quot;">​</a></h1><p>A. The Department shall seek to ensure, where appropriate, that any inspections conducted pursuant to Chapters 13 (§ <a href="http://law.lis.virginia.gov/vacode/10.1-1300/" target="_blank" rel="noreferrer">10.1-1300</a> et seq.) and 14 (§ <a href="http://law.lis.virginia.gov/vacode/10.1-1400/" target="_blank" rel="noreferrer">10.1-1400</a> et seq.) of this title and Chapter 3.1 (§ <a href="http://law.lis.virginia.gov/vacode/62.1-44.2/" target="_blank" rel="noreferrer">62.1-44.2</a> et seq.) of Title 62.1 (i) are multimedia in approach; (ii) are performed by teams of inspectors authorized to represent the air, water and solid waste programs within the Department; and (iii) minimize duplication of inspections, reporting requirements, and enforcement efforts.</p><p>B. The Department may allow any person found to be violating any law or standard for which the Department has enforcement jurisdiction to develop a plan to reduce the use or generation of toxic or hazardous substances through pollution prevention incentives or initiatives and, to the maximum extent possible, implement the plan as part of coming into compliance with the violated law or standard. This shall in no way affect the Commonwealth&#39;s ability and responsibility to seek penalties in enforcement activities.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0169" target="_blank" rel="noreferrer">169</a>.</p>',4),o=[i];function s(c,p,l,d,h,m){return t(),a("div",null,o)}const u=e(r,[["render",s]]);export{_ as __pageData,u as default};
