import{_ as e,o as a,c as i,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 10.1-1425.20 Findings and intent.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-1425.20.md","filePath":"vacode/10.1-1425.20.md"}'),t={name:"vacode/10.1-1425.20.md"},o=n('<h1 id="§-10-1-1425-20-findings-and-intent" tabindex="-1">§ 10.1-1425.20 Findings and intent. <a class="header-anchor" href="#§-10-1-1425-20-findings-and-intent" aria-label="Permalink to &quot;§ 10.1-1425.20 Findings and intent.&quot;">​</a></h1><p>A. The General Assembly finds that:</p><ol><li><p>The management of solid waste can pose a wide range of hazards to public health and safety and to the environment;</p></li><li><p>Packaging comprises a significant percentage of the overall waste stream;</p></li><li><p>The presence of heavy metals in packaging is a concern because of the potential presence of heavy metals in residue from manufacturers&#39; recycling processes, in emissions or ash when packaging is incinerated, or in leachate when packaging is landfilled; and</p></li><li><p>Lead, mercury, cadmium, and hexavalent chromium, on the basis of scientific and medical evidence, are of particular concern.</p></li></ol><p>B. It is the intent of the General Assembly to:</p><ol><li><p>Reduce the toxicity of packaging;</p></li><li><p>Eliminate the addition of heavy metals to packaging; and</p></li><li><p>Achieve reductions in toxicity without impeding or discouraging the expanded use of recovered material in the production of products, packaging, and its components.</p></li></ol><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0944" target="_blank" rel="noreferrer">944</a>.</p>',6),s=[o];function c(r,d,l,p,h,f){return a(),i("div",null,s)}const _=e(t,[["render",c]]);export{m as __pageData,_ as default};
