import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 10.1-1425.3 Inspection of battery retailers; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-1425.3.md","filePath":"vacode/10.1-1425.3.md"}'),o={name:"vacode/10.1-1425.3.md"},i=r('<h1 id="§-10-1-1425-3-inspection-of-battery-retailers-penalty" tabindex="-1">§ 10.1-1425.3 Inspection of battery retailers; penalty. <a class="header-anchor" href="#§-10-1-1425-3-inspection-of-battery-retailers-penalty" aria-label="Permalink to &quot;§ 10.1-1425.3 Inspection of battery retailers; penalty.&quot;">​</a></h1><p>The Department shall produce, print, and distribute the notices required by § <a href="http://law.lis.virginia.gov/vacode/10.1-1425.2/" target="_blank" rel="noreferrer">10.1-1425.2</a> to all places in the Commonwealth where lead acid batteries are offered for sale at retail. In performing its duties under this section, the Department may inspect any place, building, or premises subject to the provisions of § <a href="http://law.lis.virginia.gov/vacode/10.1-1425.2/" target="_blank" rel="noreferrer">10.1-1425.2</a>. Authorized employees of the Department may issue warnings to persons who fail to comply with the provisions of this article. Any person found guilty of failing to post the notice required under § <a href="http://law.lis.virginia.gov/vacode/10.1-1425.2/" target="_blank" rel="noreferrer">10.1-1425.2</a> after receiving a warning to do so pursuant to this section shall be punished by a fine of not more than fifty dollars.</p><p>1990, c. 520.</p>',3),n=[i];function s(l,p,c,d,h,f){return t(),a("div",null,n)}const m=e(o,[["render",s]]);export{u as __pageData,m as default};
