import{_ as t,o,c as s,z as e,a as n}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 10.1-1616 Exemption from taxes or assessments.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-1616.md","filePath":"vacode/10.1-1616.md"}'),a={name:"vacode/10.1-1616.md"},r=e("h1",{id:"§-10-1-1616-exemption-from-taxes-or-assessments",tabindex:"-1"},[n("§ 10.1-1616 Exemption from taxes or assessments. "),e("a",{class:"header-anchor",href:"#§-10-1-1616-exemption-from-taxes-or-assessments","aria-label":'Permalink to "§ 10.1-1616 Exemption from taxes or assessments."'},"​")],-1),i=e("p",null,"The exercise of the powers granted by this chapter is for the benefit of the people of the Commonwealth, for the increase of their commerce and prosperity, and for the improvement of their health and living conditions. Since the operation and maintenance of projects by the Authority and the undertaking of activities in furtherance of the purpose of the Authority will constitute the performance of essential governmental functions, the Authority shall not be required to pay any taxes or assessments upon any project or any property acquired or used by the Authority under the provisions of this chapter or upon the income therefrom, including sales and use taxes on tangible personal property used in the operations of the Authority. Any bonds issued under the provisions of this chapter, their transfer and the income which may result, including any profit made on the sale, shall be free from state and local taxation. The exemption hereby granted shall not be construed to extend to persons conducting business on the premises of a facility for which local or state taxes would otherwise be required.",-1),h=e("p",null,"1986, c. 360, § 10-158.13; 1988, c. 891.",-1),c=[r,i,h];function d(p,l,m,f,u,x){return o(),s("div",null,c)}const b=t(a,[["render",d]]);export{y as __pageData,b as default};
