import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 10.1-200.3 Admittance and parking in state parks; prohibitions; civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-200.3.md","filePath":"vacode/10.1-200.3.md"}'),s={name:"vacode/10.1-200.3.md"},n=i('<h1 id="§-10-1-200-3-admittance-and-parking-in-state-parks-prohibitions-civil-penalty" tabindex="-1">§ 10.1-200.3 Admittance and parking in state parks; prohibitions; civil penalty. <a class="header-anchor" href="#§-10-1-200-3-admittance-and-parking-in-state-parks-prohibitions-civil-penalty" aria-label="Permalink to &quot;§ 10.1-200.3 Admittance and parking in state parks; prohibitions; civil penalty.&quot;">​</a></h1><p>A. No person shall make use of, gain admittance to, or attempt to use or gain admittance to the facilities in any state park for the use of which a charge is assessed by the Department, unless the person pays the charge or price established by the Department.</p><p>B. No owner or driver shall cause or permit a vehicle to stand:</p><ol><li><p>Anywhere in a state park outside of designated parking spaces, except for a reasonable time in order to receive or discharge passengers; or</p></li><li><p>In any space in a state park designated for use by the handicapped unless the vehicle displays a license plate or decal issued by the Commissioner of the Department of Motor Vehicles, or a similar identification issued by a similar authority of another state or the District of Columbia, which authorizes parking in a handicap space.</p></li></ol><p>C. Any person violating any provision of this section may, in lieu of any criminal penalty, be assessed a civil penalty of twenty-five dollars by the Department. Civil penalties assessed under this section shall be paid into the Conservation Resources Fund.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0370" target="_blank" rel="noreferrer">370</a>.</p>',6),r=[n];function o(p,c,l,d,h,m){return a(),t("div",null,r)}const u=e(s,[["render",o]]);export{f as __pageData,u as default};
