import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 10.1-211 Additional duties of the Department.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-211.md","filePath":"vacode/10.1-211.md"}'),i={name:"vacode/10.1-211.md"},o=r('<h1 id="§-10-1-211-additional-duties-of-the-department" tabindex="-1">§ 10.1-211 Additional duties of the Department. <a class="header-anchor" href="#§-10-1-211-additional-duties-of-the-department" aria-label="Permalink to &quot;§ 10.1-211 Additional duties of the Department.&quot;">​</a></h1><p>In addition to other duties conferred by law, the Department shall, subject to the provisions of this article:</p><ol><li><p>Preserve the natural diversity of biological resources of the Commonwealth.</p></li><li><p>Maintain a Natural Heritage Program to select and nominate areas containing natural heritage resources for registration, acquisition, and dedication of natural areas and natural area preserves.</p></li><li><p>Develop and implement a Natural Heritage Plan that shall govern the Natural Heritage Program in the creation of a system of registered and dedicated natural area preserves.</p></li><li><p>Publish and disseminate information pertaining to natural areas and natural area preserves.</p></li><li><p>Grant permits to qualified persons for the conduct of scientific research and investigations within natural area preserves.</p></li><li><p>Provide recommendations to the Commissioner of the Department of Agriculture and Consumer Services and to the Board of Agriculture and Consumer Services on species for listing under the Virginia Endangered Plant and Insect Act, prior to the adoption of regulations therefor.</p></li><li><p>Provide recommendations to the Executive Director of the Department of Wildlife Resources and to the Board of Wildlife Resources on species for listing under the Virginia Endangered Species Act, prior to the adoption of regulations therefor.</p></li><li><p>Cooperate with other local, state and federal agencies in developing management plans for real property under their stewardship that will identify, maintain and preserve the natural diversity of biological resources of the Commonwealth.</p></li><li><p>Provide for management, development and utilization of any lands purchased, leased or otherwise acquired and enforce the provisions of this article governing natural area preserves, the stewardship thereof, the prevention of trespassing thereon, or other actions deemed necessary to carry out the provisions of this article.</p></li></ol><p>1989, c. 553; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0958" target="_blank" rel="noreferrer">958</a>.</p>',4),n=[o];function s(l,d,p,c,h,f){return t(),a("div",null,n)}const g=e(i,[["render",s]]);export{m as __pageData,g as default};
