import{_ as e,o as a,c as o,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 10.1-2301 Duties of Director.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-2301.md","filePath":"vacode/10.1-2301.md"}'),i={name:"vacode/10.1-2301.md"},r=t('<h1 id="§-10-1-2301-duties-of-director" tabindex="-1">§ 10.1-2301 Duties of Director. <a class="header-anchor" href="#§-10-1-2301-duties-of-director" aria-label="Permalink to &quot;§ 10.1-2301 Duties of Director.&quot;">​</a></h1><p>The Director shall:</p><ol><li><p>Coordinate all archaeological research on state-controlled land and in state archaeological sites and zones;</p></li><li><p>Coordinate a survey of significant archaeological sites located on state-controlled land, and upon request, survey and officially recognize significant archaeological sites on privately owned property;</p></li><li><p>Identify, evaluate, preserve and protect sites and objects of antiquity which have historic, scientific, archaeologic or educational value and are located on state-controlled land or on state archaeological sites or zones;</p></li><li><p>Protect archaeological sites and objects located on state-controlled land or on state archaeological sites or zones from neglect, desecration, damage and destruction;</p></li><li><p>Ensure that archaeological sites and objects located on state-controlled land or on state archaeological sites or zones are identified, evaluated and properly explored so that adequate records may be made;</p></li><li><p>Encourage private owners of designated state archaeological sites to cooperate with the Commonwealth to preserve the site;</p></li><li><p>Encourage a statewide archaeological education program to inform the general public of the importance of its irreplaceable archaeological heritage; and</p></li><li><p>Designate the State Archaeologist to (i) assist the Director by coordinating, overseeing, or otherwise carrying out the provisions of this chapter and (ii) perform such other duties as required by the Director. The State Archaeologist shall be a technically trained archaeologist and shall have both a practical and theoretical knowledge of archaeology.</p></li></ol><p>1977, c. 424, §§ 10-150.2, 10-150.8; 1984, c. 750; 1988, c. 891, § 10.1-901; 1989, c. 656; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0457" target="_blank" rel="noreferrer">457</a>.</p>',4),c=[r];function l(s,n,d,h,p,g){return a(),o("div",null,c)}const _=e(i,[["render",l]]);export{u as __pageData,_ as default};
