import{_ as n,o as i,c as t,z as e,a as s}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 10.1-308 Sinking fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-308.md","filePath":"vacode/10.1-308.md"}'),o={name:"vacode/10.1-308.md"},a=e("h1",{id:"§-10-1-308-sinking-fund",tabindex:"-1"},[s("§ 10.1-308 Sinking fund. "),e("a",{class:"header-anchor",href:"#§-10-1-308-sinking-fund","aria-label":'Permalink to "§ 10.1-308 Sinking fund."'},"​")],-1),r=e("p",null,"The fees, charges and revenues derived from any camping and recreational facilities subject to revenue bonds issued under the provisions of this chapter, except charges required to pay the cost of maintaining, repairing and operating such facilities and to provide fund reserves, shall be set aside in a sinking fund. The sinking fund is pledged to and charged with the payment of (i) the interest upon the bonds as it becomes due, (ii) the principal of the bonds as it becomes due, (iii) the necessary charges of paying agents for paying the interest and principal, and (iv) any premium upon bonds retired by call or purchase as provided in this chapter. The use and disposition of the sinking fund shall be subject to regulations provided in the resolution or the trust indenture. Unless otherwise provided in the resolution or trust indenture, the sinking fund shall be a fund for all such bonds without distinction or priority of one bond over another. Any moneys in the sinking fund in excess of an amount equal to one year's interest on all bonds then outstanding may be applied to the purchase or redemption of bonds.",-1),d=e("p",null,"Code 1950, § 10-107; 1966, c. 41; 1988, c. 891.",-1),c=[a,r,d];function h(u,p,l,f,g,m){return i(),t("div",null,c)}const v=n(o,[["render",h]]);export{b as __pageData,v as default};
