import{_ as e,o as t,c as i,O as s}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 10.1-529.1 Duties of district directors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-529.1.md","filePath":"vacode/10.1-529.1.md"}'),a={name:"vacode/10.1-529.1.md"},r=s('<h1 id="§-10-1-529-1-duties-of-district-directors" tabindex="-1">§ 10.1-529.1 Duties of district directors. <a class="header-anchor" href="#§-10-1-529-1-duties-of-district-directors" aria-label="Permalink to &quot;§ 10.1-529.1 Duties of district directors.&quot;">​</a></h1><p>In addition to other duties and powers, district directors shall:</p><ol><li><p>Identify soil and water issues and opportunities within the district or related to the district and establish priorities for addressing these issues;</p></li><li><p>Seek a comprehensive understanding of the complex issues that impact soil and water, and assist in resolving the identified issues at the watershed, local, regional, state, and national levels;</p></li><li><p>Engage in actions that will improve soil and water stewardship by use of locally led programs;</p></li><li><p>Increase understanding among community leaders, including elected officials and others, of their role in soil and water quality protection and improvement;</p></li><li><p>Foster discussion and advancement within the community of positions and programs by their district;</p></li><li><p>Actively participate in the activities of the district and ensure district resources are used effectively and managed wisely; and</p></li><li><p>Support and promote the advancement of districts and their capabilities.</p></li></ol><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0073" target="_blank" rel="noreferrer">73</a>.</p>',4),o=[r];function n(d,c,l,p,h,u){return t(),i("div",null,o)}const m=e(a,[["render",n]]);export{f as __pageData,m as default};
