import{_ as t,o as a,c as o,z as e,a as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 10.1-556 Determination of Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-556.md","filePath":"vacode/10.1-556.md"}'),n={name:"vacode/10.1-556.md"},r=e("h1",{id:"§-10-1-556-determination-of-board",tabindex:"-1"},[i("§ 10.1-556 Determination of Board. "),e("a",{class:"header-anchor",href:"#§-10-1-556-determination-of-board","aria-label":'Permalink to "§ 10.1-556 Determination of Board."'},"​")],-1),s=e("p",null,"The Board shall publish the result of the referendum and shall thereafter consider and determine whether the continued operation of the district within the defined boundaries is administratively practicable and feasible. If the Board determines that the continued operation of the district is administratively practicable and feasible, it shall record the determination and deny the petition. If the Board determines that the continued operation of the district is not administratively practicable and feasible, it shall record its determination and certify the determination to the district directors. In making its determination the Board shall consider the proportion of the votes cast in favor of the discontinuance of the district to the total number of votes cast, the probable expense of carrying on erosion control operations within the district, and other relevant economic and social factors. However, the Board shall not have authority to determine that the continued operation of the district is administratively practicable and feasible unless at least a majority of the votes cast in the referendum have been cast in favor of the continuance of such district.",-1),d=e("p",null,"Code 1950, § 21-109; 1964, c. 512; 1970, c. 480; 1988, c. 891.",-1),c=[r,s,d];function h(l,f,m,p,u,_){return a(),o("div",null,c)}const B=t(n,[["render",h]]);export{v as __pageData,B as default};
