import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 10.1-605.1 Delegation of powers and duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-605.1.md","filePath":"vacode/10.1-605.1.md"}'),o={name:"vacode/10.1-605.1.md"},i=r('<h1 id="§-10-1-605-1-delegation-of-powers-and-duties" tabindex="-1">§ 10.1-605.1 Delegation of powers and duties. <a class="header-anchor" href="#§-10-1-605-1-delegation-of-powers-and-duties" aria-label="Permalink to &quot;§ 10.1-605.1 Delegation of powers and duties.&quot;">​</a></h1><p>The Board may delegate to the Director or his designee any of the powers and duties vested in the Board by this article, except the adoption and promulgation of regulations. Delegation shall not remove from the Board authority to enforce the provisions of this article. At each meeting of the Board, the Director shall identify those impounding structures that are currently classified as high hazard and determined noncompliant with the spillway requirements of the Board&#39;s Impounding Structure Regulations (<a href="http://law.lis.virginia.gov/admincode/title4/agency50/chapter20/" target="_blank" rel="noreferrer">4VAC50-20</a>) or with statutory presumption provided by subsection B of § <a href="http://law.lis.virginia.gov/vacode/10.1-605/" target="_blank" rel="noreferrer">10.1-605</a>.</p><p>2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0030" target="_blank" rel="noreferrer">30</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0323" target="_blank" rel="noreferrer">323</a>.</p>',3),n=[i];function s(d,l,c,h,p,f){return t(),a("div",null,n)}const u=e(o,[["render",s]]);export{g as __pageData,u as default};
