import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 10.1-609.2 Prohibited vegetation; certain wetland vegetation allowed.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-609.2.md","filePath":"vacode/10.1-609.2.md"}'),o={name:"vacode/10.1-609.2.md"},i=n('<h1 id="§-10-1-609-2-prohibited-vegetation-certain-wetland-vegetation-allowed" tabindex="-1">§ 10.1-609.2 Prohibited vegetation; certain wetland vegetation allowed. <a class="header-anchor" href="#§-10-1-609-2-prohibited-vegetation-certain-wetland-vegetation-allowed" aria-label="Permalink to &quot;§ 10.1-609.2 Prohibited vegetation; certain wetland vegetation allowed.&quot;">​</a></h1><p>A. Dam owners shall not permit the growth of trees and other woody vegetation and shall remove any such vegetation from the slopes and crest of embankments and the emergency spillway area and within a distance of 25 feet from the toe of the embankment and abutments of the dam.</p><p>B. The provisions of subsection A shall not apply to wetland vegetation, including woody shrubs, trees, and plants, that is growing on a permanent aquatic or safety bench that has been added to the upstream embankment slope of a regulated impounding structure if such vegetation is associated with a wetland mitigation bank or in-lieu fee site that (i) has been approved by the U.S. Army Corps of Engineers and the Department of Environmental Quality and (ii) is the subject of a restrictive covenant or other permanent instrument that specifically protects the particular wetland vegetation from removal and is recorded among the land records of the locality. However, the Department may require the dam owner to remove trees by flush cutting unless the Department determines on the basis of site-specific information that the grubbing of roots is necessary to protect the integrity of the dam in a particular case.</p><p>C. Owners failing to maintain their dam in accordance with this section shall be subject to enforcement pursuant to § <a href="/vacode/10.1-613/">10.1-613</a>.</p><p>2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0030" target="_blank" rel="noreferrer">30</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0148" target="_blank" rel="noreferrer">148</a>.</p>',5),r=[i];function s(l,d,c,h,p,m){return t(),a("div",null,r)}const u=e(o,[["render",s]]);export{g as __pageData,u as default};
