import{_ as e,o,c as r,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 10.1-613.3 No liability of Board, Department, employees, or agents.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-613.3.md","filePath":"vacode/10.1-613.3.md"}'),t={name:"vacode/10.1-613.3.md"},i=a('<h1 id="§-10-1-613-3-no-liability-of-board-department-employees-or-agents" tabindex="-1">§ 10.1-613.3 No liability of Board, Department, employees, or agents. <a class="header-anchor" href="#§-10-1-613-3-no-liability-of-board-department-employees-or-agents" aria-label="Permalink to &quot;§ 10.1-613.3 No liability of Board, Department, employees, or agents.&quot;">​</a></h1><p>An owner may not bring an action against the Commonwealth, the Board, the Department, or agents or employees of the Commonwealth for the recovery of damages caused by the partial or total failure of a dam or reservoir, or by the operation of a dam or reservoir, or by an act or omission in connection with:</p><ol><li><p>Approval of the construction, alteration, or maintenance of a dam or reservoir, or approval of flood-operations plans during or after construction;</p></li><li><p>Issuance or enforcement of orders relating to maintenance or operation of the dam or reservoir;</p></li><li><p>Control or regulation of the dam or reservoir;</p></li><li><p>Measures taken to protect against failure of the dam or reservoir during an emergency;</p></li><li><p>Investigations or inspections authorized under this article;</p></li><li><p>Use of design and construction criteria prepared by the Department; or</p></li><li><p>Determination of the hazard classification of the dam.</p></li></ol><p>2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0030" target="_blank" rel="noreferrer">30</a>.</p>',4),n=[i];function s(l,p,c,d,m,f){return o(),r("div",null,n)}const g=e(t,[["render",s]]);export{_ as __pageData,g as default};
