import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 10.1-613.5 Program administration fees; establishment of Dam Safety Administrative Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-613.5.md","filePath":"vacode/10.1-613.5.md"}'),i={name:"vacode/10.1-613.5.md"},n=r('<h1 id="§-10-1-613-5-program-administration-fees-establishment-of-dam-safety-administrative-fund" tabindex="-1">§ 10.1-613.5 Program administration fees; establishment of Dam Safety Administrative Fund. <a class="header-anchor" href="#§-10-1-613-5-program-administration-fees-establishment-of-dam-safety-administrative-fund" aria-label="Permalink to &quot;§ 10.1-613.5 Program administration fees; establishment of Dam Safety Administrative Fund.&quot;">​</a></h1><p>A. The Board is authorized to establish and collect application fees from any applicant to be deposited into the Dam Safety Administrative Fund established pursuant to subsection B. Permit applications shall not be reviewed without a full payment of the required fee. Virginia Soil and Water Conservation Districts shall be exempt from all fees established pursuant to this section.</p><p>B. There is hereby created in the state treasury a special nonreverting fund to be known as the Dam Safety Administrative Fund, hereafter referred to as &quot;the Fund.&quot; The Fund shall be established on the books of the Comptroller. The Fund shall consist of permit application fees authorized under subsection A and shall be used for the administration of the dam safety program, including actions taken in accordance with §§ <a href="http://law.lis.virginia.gov/vacode/10.1-608/" target="_blank" rel="noreferrer">10.1-608</a>, <a href="http://law.lis.virginia.gov/vacode/10.1-609/" target="_blank" rel="noreferrer">10.1-609</a>, and <a href="http://law.lis.virginia.gov/vacode/10.1-613/" target="_blank" rel="noreferrer">10.1-613</a>. All such funds shall be paid into the state treasury and credited to the Fund. Interest earned on moneys in the Fund shall remain in the Fund and be credited to it. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund. Expenditures and disbursements from the Fund shall be made by the State Treasurer on warrants issued by the Comptroller upon written request signed by the Director.</p><p>2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0030" target="_blank" rel="noreferrer">30</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0013" target="_blank" rel="noreferrer">13</a>.</p>',4),s=[n];function o(d,l,h,f,m,c){return t(),a("div",null,s)}const b=e(i,[["render",o]]);export{p as __pageData,b as default};
