import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 10.1-613.6 Negotiated settlement agreements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-613.6.md","filePath":"vacode/10.1-613.6.md"}'),n={name:"vacode/10.1-613.6.md"},i=o('<h1 id="§-10-1-613-6-negotiated-settlement-agreements" tabindex="-1">§ 10.1-613.6 Negotiated settlement agreements. <a class="header-anchor" href="#§-10-1-613-6-negotiated-settlement-agreements" aria-label="Permalink to &quot;§ 10.1-613.6 Negotiated settlement agreements.&quot;">​</a></h1><p>With the consent of any owner of an impounding structure who has allegedly violated or failed, neglected, or refused to obey any regulation or order of the Board, any condition of a permit, or any provision of this chapter, the Board may enter into a negotiated settlement agreement with such owner, so long as the impounding structure or dam is not subject to the provisions of § <a href="/vacode/10.1-609/">10.1-609</a>, to correct deficiencies at the structure according to the schedule of implementation appended to the negotiated settlement agreement and for the payment of civil charges for past alleged violations in specific sums not to exceed the limit specified in § <a href="/vacode/10.1-613.2/">10.1-613.2</a>. Such civil charges shall be suspended upon compliance with the terms and conditions of the negotiated settlement agreement as determined by the Director. Such civil charges shall be instead of any appropriate civil penalty that could be imposed under § <a href="/vacode/10.1-613.2/">10.1-613.2</a> and shall be paid into the Dam Safety, Flood Prevention and Protection Assistance Fund established by Article 1.2 (§ <a href="/vacode/10.1-603.16/">10.1-603.16</a> et seq.).</p><p>2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0097" target="_blank" rel="noreferrer">97</a>.</p>',3),r=[i];function s(d,c,l,h,m,p){return t(),a("div",null,r)}const _=e(n,[["render",s]]);export{g as __pageData,_ as default};
