import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 10.1-631 Annual tax for payment of interest or to amortize indebtedness or bonds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-631.md","filePath":"vacode/10.1-631.md"}'),o={name:"vacode/10.1-631.md"},n=r('<h1 id="§-10-1-631-annual-tax-for-payment-of-interest-or-to-amortize-indebtedness-or-bonds" tabindex="-1">§ 10.1-631 Annual tax for payment of interest or to amortize indebtedness or bonds. <a class="header-anchor" href="#§-10-1-631-annual-tax-for-payment-of-interest-or-to-amortize-indebtedness-or-bonds" aria-label="Permalink to &quot;§ 10.1-631 Annual tax for payment of interest or to amortize indebtedness or bonds.&quot;">​</a></h1><p>The governing body of the watershed improvement district shall, if necessary to pay the interest on the indebtedness or bonds or to amortize such indebtedness or bonds, levy an annual tax or service charge in the manner prescribed by § <a href="http://law.lis.virginia.gov/vacode/10.1-626/" target="_blank" rel="noreferrer">10.1-626</a> on all the real estate in the watershed improvement district subject to local taxation, to satisfy such obligations. This tax, irrespective of any approvals required pursuant to § <a href="http://law.lis.virginia.gov/vacode/10.1-614/" target="_blank" rel="noreferrer">10.1-614</a>, shall be sufficient to pay interest and to amortize such indebtedness or bonds at the times required.</p><p>1956, c. 668, § 21-112.18; 1973, c. 35; 1981, c. 156; 1988, c. 891; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0105" target="_blank" rel="noreferrer">105</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0819" target="_blank" rel="noreferrer">819</a>.</p>',3),i=[n];function s(d,l,c,h,f,p){return t(),a("div",null,i)}const m=e(o,[["render",s]]);export{b as __pageData,m as default};
