import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 10.1-634.1 Conduct of referenda.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/10.1-634.1.md","filePath":"vacode/10.1-634.1.md"}'),o={name:"vacode/10.1-634.1.md"},i=a('<h1 id="§-10-1-634-1-conduct-of-referenda" tabindex="-1">§ 10.1-634.1 Conduct of referenda. <a class="header-anchor" href="#§-10-1-634-1-conduct-of-referenda" aria-label="Permalink to &quot;§ 10.1-634.1 Conduct of referenda.&quot;">​</a></h1><p>A. Except as provided in subsection B, the referenda authorized or required by this article shall be conducted pursuant to regulations prescribed by the Virginia Soil and Water Conservation Board and not as provided for under § <a href="http://law.lis.virginia.gov/vacode/24.2-684/" target="_blank" rel="noreferrer">24.2-684</a>.</p><p>B. Referenda authorized or required by this article prior to the regulations referred to in subsection A becoming effective shall be conducted by the district directors of the soil and water conservation district in which the watershed improvement district is situated pursuant to the provisions of this article as they were effective on January 1, 1995, and Article 5 (§ <a href="http://law.lis.virginia.gov/vacode/24.2-681/" target="_blank" rel="noreferrer">24.2-681</a> et seq.) of Chapter 6 of Title 24.2. The costs of holding referenda under this subsection shall be paid by the requesting landowners.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0654" target="_blank" rel="noreferrer">654</a>; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0983" target="_blank" rel="noreferrer">983</a>.</p>',4),n=[i];function s(d,c,l,f,h,p){return r(),t("div",null,n)}const g=e(o,[["render",s]]);export{_ as __pageData,g as default};
