import{_ as t,o as n,c as o,z as e,a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 11-4 Sizes of type in printed contracts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/11-4.md","filePath":"vacode/11-4.md"}'),r={name:"vacode/11-4.md"},i=e("h1",{id:"§-11-4-sizes-of-type-in-printed-contracts",tabindex:"-1"},[a("§ 11-4 Sizes of type in printed contracts. "),e("a",{class:"header-anchor",href:"#§-11-4-sizes-of-type-in-printed-contracts","aria-label":'Permalink to "§ 11-4 Sizes of type in printed contracts."'},"​")],-1),s=e("p",null,"No contract in writing entered into between a citizen of this Commonwealth and any person, firm, company or corporation, domestic or foreign, doing business in this Commonwealth, for the sale and future delivery of any goods or chattels, machinery or mechanical devices, or personal property of any kind or sort whatsoever, shall be binding upon the purchaser, where the form is printed and furnished by the person, firm, company or corporation, unless all of the provisions of such contract are clearly and plainly printed or written; and, where printed, such provisions and covenants and all stipulations as to the rights of the vendor shall be in type of not less than the size known as ten point; and, wherever in such contract, printed upon a form furnished by the vendor, it is stipulated that the vendor is not to be bound by any verbal agreement or modification of the terms of such printed contract, then such stipulation shall be printed as a separate paragraph or paragraphs and in type not smaller than pica. Should any of the contract, including the special stipulation hereinbefore mentioned, be printed in less than the size of type hereby prescribed, and the agent or salesman of such person, firm, company or corporation enter into any verbal or written or collateral agreement with the vendee, on the part of the person, firm, company or corporation, modifying or changing such printed agreement or the parts of the contract which are printed, then the vendee may, in any action instituted to enforce such contract, or the payment of any sum of money agreed to be paid under such contract, be allowed to introduce such collateral agreement, or contract in modification thereof, or any verbal statement made by the agent or salesman in modification thereof, in evidence in such action, and the same, if proved, shall be considered by the court or jury trying the case as a part of such printed contract.",-1),c=e("p",null,"1920, p. 362; Michie Code 1942, § 5562a.",-1),h=[i,s,c];function d(p,l,f,m,u,y){return n(),o("div",null,h)}const g=t(r,[["render",d]]);export{v as __pageData,g as default};
