import{_ as t,o as e,c as o,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 11-9.8 Construction of certain terms of offer to contract; use of experience modification factor prohibited.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/11-9.8.md","filePath":"vacode/11-9.8.md"}'),r={name:"vacode/11-9.8.md"},a=i('<h1 id="§-11-9-8-construction-of-certain-terms-of-offer-to-contract-use-of-experience-modification-factor-prohibited" tabindex="-1">§ 11-9.8 Construction of certain terms of offer to contract; use of experience modification factor prohibited. <a class="header-anchor" href="#§-11-9-8-construction-of-certain-terms-of-offer-to-contract-use-of-experience-modification-factor-prohibited" aria-label="Permalink to &quot;§ 11-9.8 Construction of certain terms of offer to contract; use of experience modification factor prohibited.&quot;">​</a></h1><p>A. As used in this section:</p><p>&quot;Contract&quot; means an agreement for the provision of construction services under which the contractor will be required to have and maintain a policy of insurance as defined in § <a href="http://law.lis.virginia.gov/vacode/38.2-119/" target="_blank" rel="noreferrer">38.2-119</a>.</p><p>&quot;Experience modification factor&quot; means a value assigned to an employer as determined by a rate service organization in accordance with its uniform experience rating plan required to be filed pursuant to subsection D of § <a href="http://law.lis.virginia.gov/vacode/38.2-1913/" target="_blank" rel="noreferrer">38.2-1913</a>.</p><p>&quot;Offer to contract&quot; means a solicitation of bids, Request for Proposals, or similar invitation to enter into a contract that is extended to potential contractors for construction services.</p><p>&quot;Person&quot; means any individual; firm; cooperative; association; corporation; limited liability company; trust; business trust; syndicate; partnership; limited liability partnership; joint venture; receiver; trustee in bankruptcy; club, society, or other group or combination acting as a unit; or public body, including but not limited to (i) the Commonwealth; (ii) any other state; and (iii) any agency, department, institution, political subdivision, or instrumentality of the Commonwealth or any other state.</p><p>B. A term of an offer to contract issued that requires that the successful bidder have a specified experience modification factor is prohibited.</p><p>C. Any contract or offer to contract that requires the contractor or bidder responding to the offer to contract to have a specified experience modification factor is prohibited.</p><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0754" target="_blank" rel="noreferrer">754</a>.</p>',9),n=[a];function c(s,f,d,p,u,l){return e(),o("div",null,n)}const _=t(r,[["render",c]]);export{m as __pageData,_ as default};
