import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 12.1-20 Facts to be certified by clerk upon request; signing and sealing; fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/12.1-20.md","filePath":"vacode/12.1-20.md"}'),r={name:"vacode/12.1-20.md"},o=a('<h1 id="§-12-1-20-facts-to-be-certified-by-clerk-upon-request-signing-and-sealing-fees" tabindex="-1">§ 12.1-20 Facts to be certified by clerk upon request; signing and sealing; fees. <a class="header-anchor" href="#§-12-1-20-facts-to-be-certified-by-clerk-upon-request-signing-and-sealing-fees" aria-label="Permalink to &quot;§ 12.1-20 Facts to be certified by clerk upon request; signing and sealing; fees.&quot;">​</a></h1><p>The clerk of the Commission shall, when requested, certify any one or more of the following facts:</p><ol><li><p>That a named domestic corporation is organized and existing under and by virtue of the laws of the Commonwealth and whether it is in good standing.</p></li><li><p>That a named foreign corporation of a named state or other jurisdiction is authorized to transact business in the Commonwealth and whether it is in good standing in the Commonwealth.</p></li><li><p>That a particular security has or has not been registered for sale in the Commonwealth pursuant to the provisions of the Securities Act (§ <a href="http://law.lis.virginia.gov/vacode/13.1-501/" target="_blank" rel="noreferrer">13.1-501</a> et seq.).</p></li><li><p>That a statement or other document required or permitted by law to be filed in the office of the clerk of the Commission has not been filed in that office.</p></li><li><p>The existence or nonexistence of any other fact appearing from the official records of the Commission, unless the disclosure of such fact is forbidden by law, regulation, or legal privilege.</p></li></ol><p>The certificate shall be signed by the clerk or by a member of the clerk&#39;s staff and shall be sealed with the seal of the Commission, or a facsimile thereof. Any signature may be a facsimile. When so sealed, the certificate shall be admitted in evidence in all cases, civil and criminal, as prima facie evidence of the facts contained in it.</p><p>For each certificate, the clerk shall charge and collect fees pursuant to § <a href="http://law.lis.virginia.gov/vacode/12.1-21.1/" target="_blank" rel="noreferrer">12.1-21.1</a> or subsection C of § <a href="http://law.lis.virginia.gov/vacode/12.1-21.2/" target="_blank" rel="noreferrer">12.1-21.2</a>.</p><p>Code 1950, § 12-41.1; 1962, c. 219; 1971, Ex. Sess., c. 157; 1982, c. 564; 1985, c. 522; 1991, c. 123; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0545" target="_blank" rel="noreferrer">545</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0001" target="_blank" rel="noreferrer">1</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0669" target="_blank" rel="noreferrer">669</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0446" target="_blank" rel="noreferrer">446</a>.</p>',6),n=[o];function s(l,c,f,h,d,g){return t(),i("div",null,n)}const u=e(r,[["render",s]]);export{m as __pageData,u as default};
