import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 12.1-30.1 Meetings and communications between commissioners and parties or staff.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/12.1-30.1.md","filePath":"vacode/12.1-30.1.md"}'),o={name:"vacode/12.1-30.1.md"},i=n('<h1 id="§-12-1-30-1-meetings-and-communications-between-commissioners-and-parties-or-staff" tabindex="-1">§ 12.1-30.1 Meetings and communications between commissioners and parties or staff. <a class="header-anchor" href="#§-12-1-30-1-meetings-and-communications-between-commissioners-and-parties-or-staff" aria-label="Permalink to &quot;§ 12.1-30.1 Meetings and communications between commissioners and parties or staff.&quot;">​</a></h1><p>The Commission shall after public hearing, promulgate rules of practice and procedure pursuant to § <a href="http://law.lis.virginia.gov/vacode/12.1-25/" target="_blank" rel="noreferrer">12.1-25</a> controlling meetings and communications between commissioners and any party, or between commissioners and staff concerning any fact or issue arising out of a proceeding involving the regulation of rates, charges, services or facilities of railroad, telephone, gas, electric, water, or sewer companies. The rules shall provide, among other provisions, that no commissioner shall consult with any party or any person acting on behalf of any party with respect to such proceeding without giving adequate notice and opportunity for all parties to participate.</p><p>1979, c. 343; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0191" target="_blank" rel="noreferrer">191</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>.</p>',3),r=[i];function s(c,l,m,p,d,f){return a(),t("div",null,r)}const _=e(o,[["render",s]]);export{h as __pageData,_ as default};
