import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 13.1-1018 Service on limited liability company.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-1018.md","filePath":"vacode/13.1-1018.md"}'),r={name:"vacode/13.1-1018.md"},o=a('<h1 id="§-13-1-1018-service-on-limited-liability-company" tabindex="-1">§ 13.1-1018 Service on limited liability company. <a class="header-anchor" href="#§-13-1-1018-service-on-limited-liability-company" aria-label="Permalink to &quot;§ 13.1-1018 Service on limited liability company.&quot;">​</a></h1><p>A. A domestic or foreign limited liability company&#39;s registered agent is the limited liability company&#39;s agent for service of process, notice, or demand required or permitted by law to be served on the limited liability company. The registered agent may by instrument in writing, acknowledged before a notary public, designate a natural person or persons in the office of the registered agent upon whom any such process, notice or demand may be served. Whenever any such person accepts service of process, a photographic copy of such instrument shall be attached to the return.</p><p>B. Whenever a domestic or foreign limited liability company fails to appoint or maintain a registered agent in this Commonwealth, or whenever its registered agent cannot with reasonable diligence be found at the registered office, then the clerk of the Commission shall be an agent of the limited liability company upon whom service may be made in accordance with § <a href="http://law.lis.virginia.gov/vacode/12.1-19.1/" target="_blank" rel="noreferrer">12.1-19.1</a>.</p><p>C. This section does not prescribe the only means, or necessarily the required means, of serving a domestic or foreign limited liability company.</p><p>1991, c. 168; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0517" target="_blank" rel="noreferrer">517</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0541" target="_blank" rel="noreferrer">541</a>.</p>',5),n=[o];function s(c,l,d,m,p,h){return i(),t("div",null,n)}const y=e(r,[["render",s]]);export{f as __pageData,y as default};
