import{_ as e,o as r,c as a,O as o}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 13.1-1023.1 Remedies for breach of operating agreement by member or manager.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-1023.1.md","filePath":"vacode/13.1-1023.1.md"}'),t={name:"vacode/13.1-1023.1.md"},i=o('<h1 id="§-13-1-1023-1-remedies-for-breach-of-operating-agreement-by-member-or-manager" tabindex="-1">§ 13.1-1023.1 Remedies for breach of operating agreement by member or manager. <a class="header-anchor" href="#§-13-1-1023-1-remedies-for-breach-of-operating-agreement-by-member-or-manager" aria-label="Permalink to &quot;§ 13.1-1023.1 Remedies for breach of operating agreement by member or manager.&quot;">​</a></h1><p>A. An operating agreement may provide that:</p><ol><li><p>A member or manager who fails to perform in accordance with, or to comply with terms and conditions of, the operating agreement shall be subject to specified penalties or specified consequences; and</p></li><li><p>At the time or upon the happening of events specified in the operating agreement, a member or manager shall be subject to specified penalties or specified consequences.</p></li></ol><p>The specified penalties or specified consequences may include and take the form of any penalty or consequence set forth in subsection D of § <a href="http://law.lis.virginia.gov/vacode/13.1-1027/" target="_blank" rel="noreferrer">13.1-1027</a>.</p><p>B. In the articles of organization, in writing in an operating agreement or in another writing, a member or manager may consent to or be subject to the nonexclusive jurisdiction of the courts of, or arbitration in, a specified jurisdiction, or the exclusive jurisdiction of the courts of the Commonwealth, or the exclusivity of arbitration in a specified jurisdiction of the Commonwealth, and to be served with legal process in a manner prescribed in the articles of organization, an operating agreement, or other writing.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0340" target="_blank" rel="noreferrer">340</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0601" target="_blank" rel="noreferrer">601</a>.</p>',6),n=[i];function s(c,m,p,l,f,h){return r(),a("div",null,n)}const b=e(t,[["render",s]]);export{d as __pageData,b as default};
