import{_ as e,o as i,c as o,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 13.1-1025 Limitation of liability of members and managers; exception.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-1025.md","filePath":"vacode/13.1-1025.md"}'),t={name:"vacode/13.1-1025.md"},n=a('<h1 id="§-13-1-1025-limitation-of-liability-of-members-and-managers-exception" tabindex="-1">§ 13.1-1025 Limitation of liability of members and managers; exception. <a class="header-anchor" href="#§-13-1-1025-limitation-of-liability-of-members-and-managers-exception" aria-label="Permalink to &quot;§ 13.1-1025 Limitation of liability of members and managers; exception.&quot;">​</a></h1><p>A. In any proceeding brought by or in the right of a limited liability company or brought by or on behalf of members of the limited liability company, the damages assessed against a manager or member arising out of a single transaction, occurrence or course of conduct shall not exceed the lesser of:</p><ol><li><p>The monetary amount, including the elimination of liability, specified in writing in the articles of organization or an operating agreement as a limitation on or elimination of the liability of the manager or member; or</p></li><li><p>The greater of (i) $100,000 or (ii) the amount of cash compensation received by the manager or member from the limited liability company during the twelve months immediately preceding the act or omission for which liability was imposed; however, the cash compensation of a manager or member shall not be deemed to include amounts constituting distributions for the purposes of § <a href="http://law.lis.virginia.gov/vacode/13.1-1035/" target="_blank" rel="noreferrer">13.1-1035</a>.</p></li></ol><p>B. The liability of a manager or member shall not be limited as provided in this section to the extent otherwise provided in writing in the articles of organization or an operating agreement, or if the manager or member engaged in willful misconduct or a knowing violation of the criminal law.</p><p>C. No limitation on or elimination of liability adopted pursuant to this section may be affected by any amendment of the articles of organization or operating agreement with respect to any act or omission occurring before such amendment.</p><p>1991, c. 168; 1992, c. 574; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0288" target="_blank" rel="noreferrer">288</a>.</p>',6),r=[n];function l(m,s,c,d,h,f){return i(),o("div",null,r)}const b=e(t,[["render",l]]);export{g as __pageData,b as default};
