import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse(`{"title":"§ 13.1-1041.1 Member's transferable interest subject to charging order.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-1041.1.md","filePath":"vacode/13.1-1041.1.md"}`),i={name:"vacode/13.1-1041.1.md"},o=a('<h1 id="§-13-1-1041-1-member-s-transferable-interest-subject-to-charging-order" tabindex="-1">§ 13.1-1041.1 Member&#39;s transferable interest subject to charging order. <a class="header-anchor" href="#§-13-1-1041-1-member-s-transferable-interest-subject-to-charging-order" aria-label="Permalink to &quot;§ 13.1-1041.1 Member&#39;s transferable interest subject to charging order.&quot;">​</a></h1><p>A. On application by a judgment creditor of a member or of a member&#39;s assignee, a court having jurisdiction may charge the transferable interest of the judgment debtor to satisfy the judgment. To the extent so charged, the judgment creditor has only the right to receive any distribution or distributions to which the judgment debtor would otherwise have been entitled in respect of the interest.</p><p>B. A charging order constitutes a lien on the judgment debtor&#39;s transferable interest in the limited liability company.</p><p>C. This chapter does not deprive a member or a member&#39;s assignee of a right under exemption laws with respect to the judgment debtor&#39;s interest in the limited liability company.</p><p>D. The entry of a charging order is the exclusive remedy by which a judgment creditor of a member or of a member&#39;s assignee may satisfy a judgment out of the judgment debtor&#39;s transferable interest in the limited liability company.</p><p>E. No creditor of a member or of a member&#39;s assignee shall have any right to obtain possession of, or otherwise exercise legal or equitable remedies with respect to, the property of the limited liability company.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0601" target="_blank" rel="noreferrer">601</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0912" target="_blank" rel="noreferrer">912</a>.</p>',7),n=[o];function s(c,d,h,m,l,b){return t(),r("div",null,n)}const f=e(i,[["render",s]]);export{p as __pageData,f as default};
