import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 13.1-1099.17 Plan of merger.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-1099.17.md","filePath":"vacode/13.1-1099.17.md"}'),o={name:"vacode/13.1-1099.17.md"},i=a('<h1 id="§-13-1-1099-17-plan-of-merger" tabindex="-1">§ 13.1-1099.17 Plan of merger. <a class="header-anchor" href="#§-13-1-1099-17-plan-of-merger" aria-label="Permalink to &quot;§ 13.1-1099.17 Plan of merger.&quot;">​</a></h1><p>In a merger under § <a href="/vacode/13.1-1099.16/">13.1-1099.16</a>, the plan of merger shall:</p><ol><li><p>Comply with § <a href="/vacode/13.1-1070/">13.1-1070</a>;</p></li><li><p>Include the manner and basis of converting the protected series membership interests in the canceled protected series in the manner set forth in subdivisions C 4 and 5 of § <a href="/vacode/13.1-1070/">13.1-1070</a>; and</p></li><li><p>State:</p></li></ol><p>a. For any protected series of a non-surviving series limited liability company, whether after the merger the protected series will be a relocated protected series or be dissolved, wound up, and canceled;</p><p>b. For any protected series of the surviving series limited liability company that exists before the merger, whether after the merger the protected series will be a continuing protected series or be dissolved, wound up, and canceled;</p><p>c. For each relocated protected series, its new name; and</p><p>d. For any protected series to be established by the surviving company as a result of the merger, the name of the protected series and the post office address of its principal office.</p><p>2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0636" target="_blank" rel="noreferrer">636</a>.</p>',8),s=[i];function n(d,c,l,p,h,f){return r(),t("div",null,s)}const g=e(o,[["render",n]]);export{_ as __pageData,g as default};
