import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 13.1-1099.3 Associated members.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-1099.3.md","filePath":"vacode/13.1-1099.3.md"}'),s={name:"vacode/13.1-1099.3.md"},o=i('<h1 id="§-13-1-1099-3-associated-members" tabindex="-1">§ 13.1-1099.3 Associated members. <a class="header-anchor" href="#§-13-1-1099-3-associated-members" aria-label="Permalink to &quot;§ 13.1-1099.3 Associated members.&quot;">​</a></h1><p>A. Only a member of a series limited liability company may be an associated member of a protected series of the series limited liability company.</p><p>B. A member of a series limited liability company becomes an associated member of a protected series of the series limited liability company if the operating agreement or a procedure established by the operating agreement states:</p><ol><li><p>That the member is an associated member of the protected series;</p></li><li><p>The date on which the member became an associated member; and</p></li><li><p>Any protected series membership interest the associated member has in connection with becoming or being an associated member.</p></li></ol><p>C. If a person that is an associated member of a protected series of a series limited liability company is dissociated from the series limited liability company, the person ceases to be an associated member of the protected series.</p><p>2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0636" target="_blank" rel="noreferrer">636</a>.</p>',6),r=[o];function m(c,n,d,l,p,b){return a(),t("div",null,r)}const f=e(s,[["render",m]]);export{_ as __pageData,f as default};
