import{_ as t,o as e,c as i,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 13.1-1229 Trustee standards of conduct; trustee liability; restrictions on liability limitations in governing instrument.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-1229.md","filePath":"vacode/13.1-1229.md"}'),n={name:"vacode/13.1-1229.md"},o=r('<h1 id="§-13-1-1229-trustee-standards-of-conduct-trustee-liability-restrictions-on-liability-limitations-in-governing-instrument" tabindex="-1">§ 13.1-1229 Trustee standards of conduct; trustee liability; restrictions on liability limitations in governing instrument. <a class="header-anchor" href="#§-13-1-1229-trustee-standards-of-conduct-trustee-liability-restrictions-on-liability-limitations-in-governing-instrument" aria-label="Permalink to &quot;§ 13.1-1229 Trustee standards of conduct; trustee liability; restrictions on liability limitations in governing instrument.&quot;">​</a></h1><p>A. A trustee shall discharge his duties as a trustee in accordance with the standards of conduct provided for directors of a Virginia corporation pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/13.1-690/" target="_blank" rel="noreferrer">13.1-690</a> and <a href="http://law.lis.virginia.gov/vacode/13.1-691/" target="_blank" rel="noreferrer">13.1-691</a>.</p><p>B. Subject to the provisions of subsection C, and except to the extent otherwise provided in the articles of trust or in the governing instrument of a business trust, a trustee, when acting in such capacity, is not personally liable to any person other than the business trust or a beneficial owner for any act, omission, or obligation of the business trust or any trustee.</p><p>C. A trustee or officer of a business trust shall have no liability to the business trust or a beneficial owner for any act or omission greater than that of directors or officers of a Virginia corporation to the corporation as provided in Chapter 9 (§ <a href="http://law.lis.virginia.gov/vacode/13.1-601/" target="_blank" rel="noreferrer">13.1-601</a> et seq.) of this title, including any elimination of liability provided for in the articles of trust or governing instrument.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0621" target="_blank" rel="noreferrer">621</a>.</p>',5),a=[o];function s(l,c,d,u,f,h){return e(),i("div",null,a)}const _=t(n,[["render",s]]);export{g as __pageData,_ as default};
