import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 13.1-1255 Payment of fees, fines, penalties, and interest prerequisite to Commission action; refunds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-1255.md","filePath":"vacode/13.1-1255.md"}'),s={name:"vacode/13.1-1255.md"},n=a('<h1 id="§-13-1-1255-payment-of-fees-fines-penalties-and-interest-prerequisite-to-commission-action-refunds" tabindex="-1">§ 13.1-1255 Payment of fees, fines, penalties, and interest prerequisite to Commission action; refunds. <a class="header-anchor" href="#§-13-1-1255-payment-of-fees-fines-penalties-and-interest-prerequisite-to-commission-action-refunds" aria-label="Permalink to &quot;§ 13.1-1255 Payment of fees, fines, penalties, and interest prerequisite to Commission action; refunds.&quot;">​</a></h1><p>A. The Commission shall not file or issue with respect to any domestic or foreign business trust any document or certificate specified in this chapter, except a statement of change pursuant to § <a href="/vacode/13.1-1221/">13.1-1221</a> and a statement of resignation pursuant to § <a href="/vacode/13.1-1222/">13.1-1222</a>, until all fees, fines, penalties, and interest assessed, imposed, charged, or to be collected by the Commission pursuant to this chapter or Title 12.1 have been paid by or on behalf of such business trust. Notwithstanding the foregoing, the Commission may file or issue any document or certificate with respect to a domestic or foreign business trust that has been assessed an annual registration fee if the document or certificate is filed or issued with an effective date that is on or before the due date of the business trust&#39;s annual registration fee payment in any year, provided that the Commission shall not issue a certificate of domestication with respect to a foreign business trust or a certificate of entity conversion with respect to a domestic business trust that will become a domestic stock corporation or limited liability company until the annual registration fee has been paid by or on behalf of that business trust.</p><p>B. The Commission shall have the authority to certify to the Comptroller directing refund of any overpayment of a fee, or of any fee collected for a document that is not accepted for filing, at any time within one year from the date of its payment.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0621" target="_blank" rel="noreferrer">621</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0316" target="_blank" rel="noreferrer">316</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0659" target="_blank" rel="noreferrer">659</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0810" target="_blank" rel="noreferrer">810</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0487" target="_blank" rel="noreferrer">487</a>.</p>',4),o=[n];function r(f,c,l,h,d,p){return t(),i("div",null,o)}const g=e(s,[["render",r]]);export{m as __pageData,g as default};
