import{_ as e,o as t,c as r,O as s}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 13.1-1258 Approval.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-1258.md","filePath":"vacode/13.1-1258.md"}'),o={name:"vacode/13.1-1258.md"},a=s('<h1 id="§-13-1-1258-approval" tabindex="-1">§ 13.1-1258 Approval. <a class="header-anchor" href="#§-13-1-1258-approval" aria-label="Permalink to &quot;§ 13.1-1258 Approval.&quot;">​</a></h1><p>A. Unless otherwise provided in the articles of trust or the governing instrument of a business trust, a merger shall be approved by each business trust that is to merge by the affirmative vote of the trustees and the holders of two-thirds of the outstanding beneficial interests of such business trust.</p><p>B. A merger need be approved only by the trustees of a successor business trust if:</p><ol><li><p>The merger does not reclassify or change its outstanding beneficial interests or otherwise amend its articles of trust or governing instrument; and</p></li><li><p>The beneficial interests to be issued or delivered in the merger are not more than twenty percent of the beneficial interests of the same class or series outstanding immediately before the merger becomes effective.</p></li></ol><p>C. The merger shall be approved by any other business entity party to the merger in the manner required by the articles of incorporation or charter, declaration of trust, partnership agreement, or other organizational document of the other business entity and the laws of the jurisdiction where the other business entity is organized.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0621" target="_blank" rel="noreferrer">621</a>.</p>',6),i=[a];function n(h,l,c,p,d,f){return t(),r("div",null,i)}const _=e(o,[["render",n]]);export{m as __pageData,_ as default};
