import{_ as e,o as t,c as n,O as r}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 13.1-1262 Governing instrument.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-1262.md","filePath":"vacode/13.1-1262.md"}'),i={name:"vacode/13.1-1262.md"},o=r('<h1 id="§-13-1-1262-governing-instrument" tabindex="-1">§ 13.1-1262 Governing instrument. <a class="header-anchor" href="#§-13-1-1262-governing-instrument" aria-label="Permalink to &quot;§ 13.1-1262 Governing instrument.&quot;">​</a></h1><p>A. Notwithstanding anything to the contrary contained in the governing instrument of a business trust, a governing instrument of a business trust containing a specific reference to this section may provide that an agreement of merger approved in accordance with this article may:</p><ol><li><p>Effect any amendment to the governing instrument of the business trust; or</p></li><li><p>Effect the adoption of a new governing instrument of the business trust if it is the successor trust in the merger.</p></li></ol><p>B. 1. Any amendment to the governing instrument of a business trust or adoption of a new governing instrument of the business trust made under this section shall be effective at the effective time or date of the merger.</p><ol start="2"><li>The provisions of this subsection shall not be construed to limit the accomplishment of a merger or of any of the matters referred to herein by any other means provided for in the governing instrument of a business trust or other agreement or as otherwise permitted by law, including that the governing instrument of any constituent business trust to the merger shall be the governing instrument of the successor trust.</li></ol><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0621" target="_blank" rel="noreferrer">621</a>.</p>',6),s=[o];function a(c,h,u,m,f,g){return t(),n("div",null,s)}const p=e(i,[["render",a]]);export{d as __pageData,p as default};
