import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 13.1-1280 Scope of inspection right.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-1280.md","filePath":"vacode/13.1-1280.md"}'),a={name:"vacode/13.1-1280.md"},i=r('<h1 id="§-13-1-1280-scope-of-inspection-right" tabindex="-1">§ 13.1-1280 Scope of inspection right. <a class="header-anchor" href="#§-13-1-1280-scope-of-inspection-right" aria-label="Permalink to &quot;§ 13.1-1280 Scope of inspection right.&quot;">​</a></h1><p>A. A beneficial owner&#39;s agent or attorney has the same inspection and copying rights as the beneficial owner he represents.</p><p>B. The right to copy records under § <a href="http://law.lis.virginia.gov/vacode/13.1-1279/" target="_blank" rel="noreferrer">13.1-1279</a> includes, if reasonable, the right to receive copies made by photographic or other means.</p><p>C. The business trust may impose a reasonable charge, covering the costs of labor and material, for copies of any documents provided to the beneficial owner. The charge may not exceed the estimated cost of production or reproduction of the records.</p><p>D. The business trust may comply with a beneficial owner&#39;s demand to inspect the record of beneficial owners under subdivision 3 of subsection B of § <a href="http://law.lis.virginia.gov/vacode/13.1-1279/" target="_blank" rel="noreferrer">13.1-1279</a> by providing him with a list of its beneficial owners that was compiled no earlier than the date of the beneficial owner&#39;s demand.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0621" target="_blank" rel="noreferrer">621</a>.</p>',6),n=[i];function s(c,h,p,d,l,f){return o(),t("div",null,n)}const m=e(a,[["render",s]]);export{g as __pageData,m as default};
