import{_ as t,o,c as a,z as e,a as i}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 13.1-301.1 Amendments to articles of incorporation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/13.1-301.1.md","filePath":"vacode/13.1-301.1.md"}'),n={name:"vacode/13.1-301.1.md"},s=e("h1",{id:"§-13-1-301-1-amendments-to-articles-of-incorporation",tabindex:"-1"},[i("§ 13.1-301.1 Amendments to articles of incorporation. "),e("a",{class:"header-anchor",href:"#§-13-1-301-1-amendments-to-articles-of-incorporation","aria-label":'Permalink to "§ 13.1-301.1 Amendments to articles of incorporation."'},"​")],-1),r=e("p",null,"An association may amend its articles of incorporation by the affirmative vote of two-thirds of the members voting thereon at any regular meeting, or at a special meeting called for the purpose. Notice of the proposed amendment and of the time and place of holding such meetings shall be delivered to each member, or mailed to his last known address shown by the books of the association, at least ten days prior to any such meetings. No amendment affecting the priority or preferential rights of any outstanding nonvoting stock shall be adopted until the written consent of two-thirds of the holders of such outstanding nonvoting stock has been obtained. Triplicate originals of the articles of amendment duly signed and acknowledged together with the filing fee required to be paid shall be delivered to the Commission. If the Commission finds that the articles comply with the requirements of law and that all required fees have been paid, it shall by order issue a certificate of amendment, which shall be admitted to record in its office. Upon the issuance of such certificate, it shall become effective in accordance with its terms.",-1),c=e("p",null,"1958, c. 88.",-1),d=[s,r,c];function l(h,m,f,p,u,_){return o(),a("div",null,d)}const v=t(n,[["render",l]]);export{b as __pageData,v as default};
